mapper.select:
SELECT product_id, product_name, description, brand, price, stock, category_id, image_url, created_at, discounted_price
FROM PRODUCT
WHERE product_id = #{productId}

SELECT p.* FROM PRODUCT p JOIN CATEGORY c ON p.category_id = c.category_id
AND c.CODE IN
#{code}
AND ( p.brand LIKE CONCAT('%', #{searchText}, '%') OR p.description LIKE CONCAT('%', #{searchText}, '%') OR p.product_name LIKE CONCAT('%', #{searchText}, '%') )
ORDER BY p.product_id LIMIT 20 OFFSET #{offset}

select * from PRODUCT_IMAGE
WHERE product_id = #{product_Id}
ORDER BY image_order ASC

select * from PRODUCT_SIZE
WHERE product_id = #{productId}
ORDER BY size_code asc
