mapper.update:
UPDATE INQUIRY
SET reply = #{reply}, status = #{status}, updated_at = NOW()
WHERE inquiry_id = #{inquiryId}

mapper.select:
SELECT i.inquiry_id, i.user_id, u.user_name, i.order_id, i.subject, i.content, i.category AS categoryCode, i.status AS statusCode, i.reply, i.created_at, i.updated_at, i.product_id, p.product_name, imgs.image_url FROM INQUIRY i JOIN USER u ON i.user_id = u.user_id LEFT JOIN PRODUCT p ON i.product_id = p.product_id LEFT JOIN INQUIRY_IMAGE imgs ON i.inquiry_id = imgs.inquiry_id
AND i.category IN
#{categoryId}
AND i.status IN
#{statusCode}
ORDER BY i.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM INQUIRY i


        AND i.category IN

            #{categoryId}



        AND i.status IN

            #{statusCode}

SELECT COUNT(*)
FROM INQUIRY_IMAGE
WHERE inquiry_id = #{inquiryId}
